        }
    }

    async Task VerificExit(string cedula)
    {
        candidatosList = await http.GetFromJsonAsync<List<Candidato>>($"https://localhost:44322/api/Candidatos/Cedula/{cedula}");
        if (candidatosList.Count != 0)
        {
            candidate = candidatosList[0];
